import React from 'react'
import axios from 'axios';
import Post from '../Post/Post'




export default class Posts extends React.Component {

    state = {
        posts: []
    };

    componentDidMount() {

        this.getData();

    }

    getData = () => {
        axios
            .get("https://blog-app-server1.herokuapp.com/api")
            .then(data => {
                this.setState({ posts: data.data });

            })
            .catch(err => { console.log(err); return null; });
    };

    render() {
        return (

            <div className="postsa-container">

                {this.state.posts.find(post => {

                    if (post._id === "5e78ea43c5363f0bc187dc97") {
                        console.log(post)
                        return (<h1>{post.text}</h1>)
                    }
                })}
                {/* <h1>{value.location.state.fromNotifications.postsa.title}</h1> */}
            </div>
        )
    }
}
