import React from 'react'
import axios from 'axios';
import Post from '../Post/Post'
import './PostPage.css'




export default class Posts extends React.Component {

    state = {
        posts: []
    };

    componentDidMount() {

        this.getData();

    }

    getData = () => {
        axios
            .get("https://blog-app-server1.herokuapp.com/api")
            .then(data => {
                console.log(data)

                this.setState({ posts: data.data });

            })
            .catch(err => { console.log(err); return null; });
    };

    render() {
        return (

            <div className="postsa-container">

                {this.state.posts.map((post, i) => {
                    if (post._id === this.props.match.params.id) {

                        return <>
                            <main className="postPage-container">
                                <section className="first-col">

                                    <div className="title-container">
                                        < p id="title"key={post.id} >{post.title}</p></div>

                                        <div className="image-container">
                                        <img className="PostImage" key={post.id} src={post.image}></img></div>
                                </section>
                                <section className="second-col">
                                    <p key={post.id}>{post.Date.substr(0, 10)}</p>
                                    <p key={post.id}>{post.text}</p>
                                </section>
                            </main>
                        </>
                    }
                })}

            </div >
        )
    }
}
