    constructor(props) {
        super(props);
        this.state = {
            blogPosts: []
        }
    }

    componentDidMount() {
        fetch('http://localhost:1200/api').then(res => res.json()).then({ blogPosts: data });
    }

    render() {
        return (

            <div>
                {blogPosts.map((post, i) => <Post key={i} post={post} i={i} />)}

            </div>

        )
    }
}

