import React from 'react'
import { Link } from 'react-router-dom';

import './Post.css'

const Post = ({ post: { _id, title, text, description }, i }) => {
    return (
        <div className="post-container">
            <Link to={{ pathname: '/post/' + _id, state: { post: _id } }} >   <h2 className="heading">{title}</h2></Link>


            {/* <img src={img}></img> */}
            <p className="text">{text}</p>
            <p className="description">{description}</p>
            <div className="info">
                <a>number:{i + 1}</a>
            </div>
        </div>
    )
}

export default Post
