import React from 'react'
import Post from '../Post/Post'
import axios from 'axios';
import './Posts.css'

export default class Posts extends React.Component {

    state = {

        posts: []
    };

    componentDidMount() {

        this.getData()
            .then((res) => { this.setState({ promiseIsResolved: true }) });
    }

    getData = async () => {

        await axios
            .get("https://blog-app-server1.herokuapp.com/api")
            .then(data => {
                this.setState({ posts: data.data })
            })
            .catch(err => { console.log(err); return null; });

        this.changeDate();
    };

    changeDate = () => {

        { this.state.posts.map((post) => { post.Date = post.Date.substring(0, 10) }) }

    }

    render() {

        if (!this.state.promiseIsResolved) { return null }

        return (

            <div className="posts-container">

                {this.state.posts.map((post, i) => <Post key={i} post={post} i={i} />)}

            </div>
        )
    }
}
