import React from 'react'

const Post = ({ post: { title, text, description }, i }) => {
    return (
        <div className="post-container">
            <h2 className="heading">{title}</h2>
            {/* <img src={img}></img> */}
            <p className="text">{text}</p>
            <p className="description">{description}</p>
            <a>number:{i + 1}</a>
        </div>
    )
}

export default Post
